export interface ControlPolicyControlPolicyMint {
  new (starterUtxo: CardanoTransactionOutputReference): Script;
  action: TypesControlAction;
}

export const ControlPolicyControlPolicyMint = Object.assign(
  function (starterUtxo: CardanoTransactionOutputReference) {
    return {
      type: "PlutusV3",
      script: applyParamsToScript(
        [starterUtxo],
        "",
        {
          shape: {
            dataType: "list",
            items: [
              {
                $ref: "#/definitions/cardano/transaction/OutputReference",
              },
            ],
          },
          definitions,
        } as any
      ),
    };
  },
  {
    action: {
      shape: { $ref: "#/definitions/types/ControlAction" },
      definitions,
    },
  }
) as unknown as ControlPolicyControlPolicyMint;

